package systemcontract

import (
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/consensus/parlia/vmcaller"
	"github.com/ethereum/go-ethereum/core"
	"github.com/ethereum/go-ethereum/core/state"
	"github.com/ethereum/go-ethereum/core/types"
	"github.com/ethereum/go-ethereum/log"
	"github.com/ethereum/go-ethereum/params"
	"math"
	"math/big"
)

const (
	RewardHomeCode = ""
)

var (
	rewardHomeAdmin        = common.HexToAddress("0xffe642ecf5f51caa422e5f814483f6d989a6cc1b")
	rewardHomeAdminTestnet = common.HexToAddress("0x83c4a11940ebcc04ac8a03cc2d290e177cccdf0f")
)

type hardForkRewardHome struct {
}

func (s *hardForkRewardHome) GetName() string {
	return RewardHomeContractName
}
func (s *hardForkRewardHome) getAdminByChainId(chainId *big.Int) common.Address {
	if chainId.Cmp(params.MainnetChainConfig.ChainID) == 0 {
		return rewardHomeAdmin
	}

	return rewardHomeAdminTestnet
}

func (s *hardForkRewardHome) Update(config *params.ChainConfig, height *big.Int, state *state.StateDB) (err error) {
	contractCode := common.FromHex(RewardHomeCode)

	//write code to sys contract
	state.SetCode(RewardHomeContractAddr, contractCode)
	log.Debug("Write code to system contract account", "addr", RewardHomeContractAddr.String(), "code", contractCode)

	return
}

func (s *hardForkRewardHome) Execute(state *state.StateDB, header *types.Header, chainContext core.ChainContext, config *params.ChainConfig) (err error) {
	// initialize v1 contract
	method := "initialize"
	stakingPerPower := new(big.Int).SetUint64(16000000000000)
	gasPerPower := new(big.Int).SetUint64(4000000000000)
	data, err := GetInteractiveABI()[s.GetName()].Pack(method, s.getAdminByChainId(config.ChainID), stakingPerPower, gasPerPower)
	if err != nil {
		log.Error("Can't pack data for initialize", "error", err)
		return err
	}

	msg := types.NewMessage(header.Coinbase, &RewardHomeContractAddr, 0, new(big.Int), math.MaxUint64, new(big.Int), data, nil, false)
	_, err = vmcaller.ExecuteMsg(msg, state, header, chainContext, config)

	return
}
